mapper.select:
SELECT MEMBER_CODE , ID , PASSWORD , NAME , PHONE , ADDRESS , MEMBER_STATUS FROM MEMBER
AND ID = #{id}
AND MEMBER_CODE = #{memberCode}
ORDER BY MEMBER_CODE

mapper.update:
UPDATE MEMBER
SET PASSWORD = #{password}
    , NAME = #{name}
    , PHONE = #{phone}
    , ADDRESS = #{address}

        , MEMBER_STATUS = #{memberStatus}

WHERE MEMBER_CODE = #{memberCode}
