mapper.insert:
INSERT INTO TRANSACTION
    (SILAGE_CODE, SELLER_CODE, BUYER_CODE, BANK_NAME, ACCOUNT_NO, TOTAL_PRICE, TRANSACTION_DATETIME)
VALUES
    (#{silageCode}
    , #{sellerCode}
    , #{buyerCode}
    , #{bankName}
    , #{accountNo}
    , #{totalPrice}
    , STR_TO_DATE(NOW(),'%Y-%m-%d %H:%i:%s'))

mapper.select:
SELECT TRANSACTION_CODE , SILAGE_CODE , ID , BUYER_CODE , (SELECT ID FROM MEMBER WHERE MEMBER_CODE = TRANSACTION.SELLER_CODE) SELLER_ID , SELLER_CODE , BANK_NAME , ACCOUNT_NO , TOTAL_PRICE , TRANSACTION_DATETIME , DEPOSIT_STATUS , REMIT_STATUS FROM TRANSACTION INNER JOIN MEMBER ON TRANSACTION.BUYER_CODE = MEMBER.MEMBER_CODE
AND TRANSACTION_DATETIME BETWEEN #{startDate} AND #{endDate}
AND SILAGE_CODE = #{silageCode}
AND TRANSACTION_CODE = #{transactionCode}
AND SELLER_CODE = #{sellerCode}
AND BUYER_CODE=#{buyerCode}
AND SELLER_CODE = #{sellerCode} OR BUYER_CODE=#{buyerCode}
ORDER BY TRANSACTION_DATETIME
LIMIT #{pageNo}, 10

SELECT TRANSACTION_CODE , SILAGE_CODE , ID , BUYER_CODE , (SELECT ID FROM MEMBER WHERE MEMBER_CODE = TRANSACTION.SELLER_CODE) SELLER_ID , SELLER_CODE , BANK_NAME , ACCOUNT_NO , TOTAL_PRICE , TRANSACTION_DATETIME , DEPOSIT_STATUS , REMIT_STATUS FROM TRANSACTION INNER JOIN MEMBER ON TRANSACTION.BUYER_CODE = MEMBER.MEMBER_CODE
AND SILAGE_CODE = #{silageCode}
AND TRANSACTION_CODE = #{transactionCode}
AND SELLER_ID = ${sellerId}
ORDER BY TRANSACTION_DATETIME

mapper.update:
UPDATE TRANSACTION
SET DEPOSIT_STATUS = #{depositStatus},
    REMIT_STATUS = #{remitStatus}
WHERE TRANSACTION_CODE = #{transactionCode}
